mapper.update:
UPDATE QUESTBOARD
SET QST_FAST='Y'
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_CONTENT=#{qst_content}, QST_SPECIES=#{qst_species}, QST_PART=#{qst_part}
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_STATUS ='R'
WHERE QST_SEQ=#{qst_seq}

UPDATE QUESTBOARD
SET QST_STATUS ='N'
WHERE QST_SEQ=#{qst_seq}

UPDATE REPLYBOARD
SET RPY_CONTENT=#{rpy_content}
WHERE RPY_SEQ = #{rpy_seq}

UPDATE REPLYBOARD 
SET RPY_STATUS ='N'
WHERE RPY_REF = #{rpy_ref}

UPDATE REPLYBOARD 
SET QST_STATUS ='R'
WHERE QST_SEQ = #{qst_seq}

UPDATE REPLYBOARD 
SET RPY_CHOSEN ='Y'
WHERE RPY_SEQ = #{rpy_seq}
